package org.netbeans.rest.application.config;

import java.util.Set;
import javax.ws.rs.core.Application;

/**
 *
 * @author jarltuxen
 */
@javax.ws.rs.ApplicationPath("webresources")
public class ApplicationConfig extends Application {

    @Override
    public Set<Class<?>> getClasses() {
        return getRestResourceClasses();
    }

    /**
     * Do not modify this method. It is automatically generated by NetBeans REST support.
     */
    private Set<Class<?>> getRestResourceClasses() {
        Set<Class<?>> resources = new java.util.HashSet<Class<?>>();
        resources.add(WebService.service.CustomerFacadeREST.class);
        resources.add(WebService.service.DiscountCodeFacadeREST.class);
        resources.add(WebService.service.ManufacturerFacadeREST.class);
        resources.add(WebService.service.MicroMarketFacadeREST.class);
        resources.add(WebService.service.ProductCodeFacadeREST.class);
        resources.add(WebService.service.ProductFacadeREST.class);
        resources.add(WebService.service.PurchaseOrderFacadeREST.class);
        return resources;
    }
    
}
